package reviewJavaClass04;

public class SimpleWhileLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int var = 0;

		while (var <= 10) {
			System.out.println("Value of var " + var);
			var++;
		}
		
		System.out.println("------------------------------");
		var = 10;

		while (var >= 0) {
			System.out.println("Value of var " + var);
			var--;
		}
		
	}
}
